import React from "react";

import { Text, View, StyleSheet } from "@react-pdf/renderer";
import List,{Item} from './List'

const styles = StyleSheet.create({
  container: {
    marginBottom: 10,
  },
  title: {
    fontSize: 14,
    fontFamily: 'Fredoka',
    textTransform: "uppercase",
  },
  heading: {
    fontSize: 12,
    fontFamily: 'Roboto-Medium',
    marginTop: 10,
  },
  subHeading: {
    fontSize: 18,
    fontFamily: 'Roboto-Bold',
    marginTop: 8,
    display: 'flex',
    flexWrap: 'wrap'
  },
  text: {
    fontSize: 10,
    marginLeft: 4,
    color: "grey"
  },
  line: {
    backgroundColor: "#DFDFDF",
    height: 1,
    width: '100%'
  },
  educationBlk: {
    display: 'flex',
    flexDirection: 'row'
  },
  educationInBlk1: {
    display: 'flex',
    flexDirection: 'column',
    flex: 2,
    flexWrap: 'wrap'
  },
  educationInBlk2: {
    display: 'flex',
    flexDirection: 'column',
    flex: 4,
    flexWrap: 'wrap'
  }
  //   course:{
  //       border:.5,
  //       borderColor:"black",
  //       padding:4,
  //       borderRadius:10,
  //   }
});

export default function Education({ data }) {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Education</Text>
      <View style={styles.line}></View>
      <View style={styles.educationBlk}>
        <View style={styles.educationInBlk1}>
          <Text style={styles.heading}>Tenth Education</Text>
        </View>
        <View style={styles.educationInBlk2}>
          <Text style={styles.subHeading}>{data.acadDetails.SchoolName10}, {data.acadDetails.Location10}</Text>
          
          <Text style={styles.text}>Cgpa/Percentage : {data.acadDetails.Cgpa10}</Text>
          <Text style={styles.text}>Board: {data.acadDetails.Board10}</Text>
        </View>
      </View>


      {
        data.degreeDetails.map(degree => {
          return (
            <View style={styles.course}>
              <Text style={styles.heading}>{degree.CollegeName}</Text>
              <Text style={styles.text}>Course : {degree.Degree}</Text>
              <Text style={styles.text}>Cgpa/Percentage : {degree.Cgpa}</Text>
              <Text style={styles.text}>Location : {degree.Location}</Text>
            </View>
          )
        })
      }
      <View stye={styles.course}>
        <Text style={styles.heading}>12th Education</Text>
        <Text style={styles.subHeading}>{data.acadDetails.SchoolName12}</Text>
        <Text style={styles.text}>Cgpa/Percentage : {data.acadDetails.Cgpa12}</Text>
        <Text style={styles.text}>Board: {data.acadDetails.Board12}</Text>
        <Text style={styles.text}>Location: {data.acadDetails.Location12}</Text>
      </View>
      <View stye={styles.course}>
        <Text style={styles.heading}>10th Education</Text>
        <Text style={styles.subHeading}>{data.acadDetails.SchoolName10}</Text>
        <Text style={styles.text}>Cgpa/Percentage : {data.acadDetails.Cgpa10}</Text>
        <Text style={styles.text}>Board: {data.acadDetails.Board10}</Text>
        <Text style={styles.text}>Location: {data.acadDetails.Location10}</Text>
      </View>
    </View>
  );
}
